import { Component } from '@angular/core';

interface Education {
  degree: string;
  institution: string;
  year: string;
  achievements: string[];
}

@Component({
  selector: 'app-education',
  template: `
    <section class="education-section py-5">
      <div class="container">
        <h2 class="text-center mb-5" data-aos="fade-up">Education</h2>
        <div class="timeline">
          <div class="timeline-item" *ngFor="let edu of education" data-aos="fade-up">
            <div class="card">
              <div class="card-body">
                <h3 class="card-title">{{ edu.degree }}</h3>
                <h4 class="h6 text-muted">{{ edu.institution }}</h4>
                <p class="text-muted">{{ edu.year }}</p>
                <ul class="list-unstyled">
                  <li class="mb-2" *ngFor="let achievement of edu.achievements">
                    <i class="bi bi-check-circle-fill text-success me-2"></i>
                    {{ achievement }}
                  </li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  `,
  styles: [`
    .timeline {
      position: relative;
      max-width: 800px;
      margin: 0 auto;
    }
    .timeline-item {
      margin-bottom: 2rem;
      position: relative;
      padding-left: 2rem;
    }
    .timeline-item::before {
      content: '';
      position: absolute;
      left: 0;
      top: 0;
      height: 100%;
      width: 2px;
      background: #dee2e6;
    }
    .timeline-item::after {
      content: '';
      position: absolute;
      left: -6px;
      top: 0;
      width: 14px;
      height: 14px;
      border-radius: 50%;
      background: #007bff;
    }
    .card {
      border: none;
      box-shadow: 0 0 15px rgba(0,0,0,.1);
    }
  `]
})
export class EducationComponent {
  education: Education[] = [
    {
      degree: 'Master of Science in Computer Science',
      institution: 'University Name',
      year: '2020-2022',
      achievements: [
        'Specialized in Artificial Intelligence and Machine Learning',
        'Published research paper on Deep Learning applications',
        'Graduate Teaching Assistant for Data Structures course',
        'GPA: 3.9/4.0'
      ]
    },
    {
      degree: 'Bachelor of Engineering in Computer Science',
      institution: 'University Name',
      year: '2016-2020',
      achievements: [
        'First Class Honours',
        'Led the university programming team',
        'Developed an award-winning project in IoT',
        'Dean\'s List all semesters'
      ]
    }
  ];
}
