import { Component } from '@angular/core';

interface Skill {
  name: string;
  level: number;
  category: string;
}

@Component({
  selector: 'app-skills',
  template: `
    <section class="skills-section py-5">
      <div class="container">
        <h2 class="text-center mb-5" data-aos="fade-up">Skills</h2>
        
        <div class="row" *ngFor="let category of categories" class="mb-5">
          <div class="col-12">
            <h3 class="h4 mb-4" data-aos="fade-up">{{ category }}</h3>
            <div class="row g-4">
              <div class="col-md-6" *ngFor="let skill of getSkillsByCategory(category)" data-aos="fade-up">
                <div class="skill-item">
                  <div class="d-flex justify-content-between mb-1">
                    <span class="skill-name">{{ skill.name }}</span>
                    <span class="skill-percentage">{{ skill.level }}%</span>
                  </div>
                  <div class="progress">
                    <div class="progress-bar" role="progressbar"
                         [style.width.%]="skill.level"
                         [attr.aria-valuenow]="skill.level"
                         aria-valuemin="0"
                         aria-valuemax="100">
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  `,
  styles: [`
    .skill-item {
      margin-bottom: 1.5rem;
    }
    .progress {
      height: 10px;
      border-radius: 5px;
      background-color: #e9ecef;
    }
    .progress-bar {
      background: linear-gradient(90deg, #007bff, #00bcd4);
      border-radius: 5px;
      transition: width 1s ease-in-out;
    }
    .skill-name {
      font-weight: 500;
    }
    .skill-percentage {
      color: #6c757d;
    }
  `]
})
export class SkillsComponent {
  skills: Skill[] = [
    // Frontend
    { name: 'HTML/CSS', level: 95, category: 'Frontend Development' },
    { name: 'JavaScript/TypeScript', level: 90, category: 'Frontend Development' },
    { name: 'Angular', level: 85, category: 'Frontend Development' },
    { name: 'React', level: 80, category: 'Frontend Development' },
    
    // Backend
    { name: 'Node.js', level: 85, category: 'Backend Development' },
    { name: 'Python', level: 80, category: 'Backend Development' },
    { name: 'Java', level: 75, category: 'Backend Development' },
    { name: 'SQL', level: 85, category: 'Backend Development' },
    
    // DevOps & Tools
    { name: 'Git', level: 90, category: 'DevOps & Tools' },
    { name: 'Docker', level: 75, category: 'DevOps & Tools' },
    { name: 'AWS', level: 70, category: 'DevOps & Tools' },
    { name: 'CI/CD', level: 75, category: 'DevOps & Tools' }
  ];

  get categories(): string[] {
    return [...new Set(this.skills.map(skill => skill.category))];
  }

  getSkillsByCategory(category: string): Skill[] {
    return this.skills.filter(skill => skill.category === category);
  }
}
